// Function to look up product details using UPCitemdb API via a POST request.
// (For production, consider moving this to an API route to bypass CORS issues.)
async function lookupProduct(barcode) {
    try {
        const response = await fetch("https://api.upcitemdb.com/prod/trial/lookup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                upc: barcode
            })
        });
        if (!response.ok) {
            throw new Error("Product lookup failed: " + response.statusText);
        }
        const data = await response.json();
        return data;
    } catch (error) {
        console.error("Error fetching product details:", error);
        return null;
    }
}
